import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

import logo from './img/logo.png';
import back from './img/back1.png';
import './css/addFeedback.css';



class sendEmail extends Component{
    constructor(props){
        super(props);

       

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            type: '',
            email: '',
            message:''
        }
    }
    onChangeType(e){
        this.setState( {
            type: e.target.value
        });
    }
    onChangeEmail(e){
        this.setState( {
            email: e.target.value
        });
    }
    onChangeMessage(e){
        this.setState( {
            message: e.target.value
        });
    }
    onSubmit(e){
        e.preventDefault();
       
        const obj = {
            type : this.state.type,
            email : this.state.email,
            message : this.state.message
        };

                        axios.post('http://localhost:4000/jacklup/addfeedback',obj)
                        .then(res => {
                            alert("Feedback Send Successfully");
                            this.setState({
                                type: '',
                                email: '',
                                message:''
                    
                            })
                            console.log(res.data)});
                        window.location.replace('/');
                    
    }

  render() {
    return(
        <div>
            <div className='content'>

                <div className='logo'>
                    <a href = "/"><img src = {logo}/></a>
                </div>

                
                <br/><br/><br/><br/>
                <h4>Send your message </h4>
                <br/>
                <div className='row'>
                    <div className='col-lg-8'>
                        <div className='outer'>
                            <h5>Contact our Member</h5>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>Name :</label>
                                    <input type ="text" required  className="form-control" />
                                </div>
                                <div className="form-group">
                                    <label>E-mail :</label>
                                    <input type ="email" required  className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <label>Message</label>
                                   <textarea required  className="form-control"></textarea>
                                </div>
                                <div className="form-group">
                                    <input type = "submit" value = "Send" className = "btn" style={{background:'purple',color:'white'}} />
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className='col-lg-4'>
                          <img src = {back} width="200"/>
                    </div>
                    
                </div>
            </div>
        </div>
    );
  }
}

export default sendEmail;
